import {
  Center,
  Container,
  createStyles,
  Group,
  Header as MantineHeader,
  MantineNumberSize,
  Text,
} from '@mantine/core';
import { Dispatch, SetStateAction } from 'react';

const useStyles = createStyles((theme) => ({
  spacedHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    height: '100%',
    backgroundColor: '#F6F5F6',
    // borderBottom: '2px solid #1CAD73'
  },

  blur: {
    background: theme.colorScheme === 'dark' ? 'rgba(30, 30, 30, 0.8)' : 'rgba(255, 255, 255, 0.8)',
    boxShadow:
      theme.colorScheme === 'dark'
        ? '0 -2px 40px rgba(0, 0, 0, 1)'
        : '0 4px 40px rgba(0, 0, 0, 0.1)',
    backdropFilter: 'saturate(180%) blur(10px)',
    webkitBackdropFilter: 'saturate(180%) blur(10px)',
  },
}));

type MainHeaderProps = {
  height: number;
  padding: MantineNumberSize;
};

function MainHeader({ ...headerProps }: MainHeaderProps) {
  const { classes } = useStyles();

  return (
    <MantineHeader className={classes.blur} {...headerProps}>
      <Container className={classes.spacedHeader} fluid>
        
        <Center style={{ width: 1500, height: 200 }}>
        <Group spacing='xs'>
          <Text
            size={50}
            weight={'bold'}
          >
            SMART - SERVE
          </Text>
        </Group>
        </Center>
      </Container>
    </MantineHeader>
  );
}

export default MainHeader;
