import Bacardi from '../images/Bacardi.webp';
import Daiqiri from '../images/Daiqiri.jpeg';
import GreyGoose from '../images/GreyGoose.jpeg';
import Jagermeister from '../images/Jagermeister.jpeg';
import Licor43 from '../images/Licor43.jpeg';
import Limoncello from '../images/Limoncello.jpeg';
import MoscowMule from '../images/MoscowMule.webp';
import Negroni from '../images/Negroni.jpeg';
import OldFashioned from '../images/OldFashioned.jpeg';
import PinaColada from '../images/PinaColada.jpeg';
import Soju from '../images/Soju.jpeg';

type Category = 'cocktails' | 'spirits' | 'liqueurs';

export interface Drink {
  name: string;
  image: string;
  category: Category;
}

interface DrinkSection {
  category: Category;
  drinks: Array<Drink>;
}

const info: Array<DrinkSection> = [
  {
    category: 'cocktails',
    drinks: [
      {
        name: 'Negroni',
        price: '$10.70',
        rating: 4,
        image: Negroni,
        volume: '6 oz',
        category: 'cocktails',
      },
      {
        name: 'Daiquiri',
        price: '$12.05',
        rating: 5,
        image: Daiqiri,
        volume: '12 oz',
        category: 'cocktails',
      },
      {
        name: 'Old Fashioned',
        price: '$9.05',
        rating: 2,
        image: OldFashioned,
        volume: '8 oz',
        category: 'cocktails',
      },
      {
        name: 'Piña Colada',
        price: '$6.70',
        rating: 4,
        image: PinaColada,
        volume: '12 oz',
        category: 'cocktails',
      },
      {
        name: 'Moscow Mule',
        price: '$13.20',
        rating: 2,
        image: MoscowMule,
        volume: '8 oz',
        category: 'cocktails',
      },
    ],
  },
  {
    category: 'spirits',
    drinks: [
      {
        name: 'Grey Goose',
        price: '$5.95',
        rating: 4,
        image: GreyGoose,
        volume: '1 oz',
        category: 'spirits',
      },
      {
        name: 'Bacardi',
        price: '$7.00',
        rating: 2,
        image: Bacardi,
        volume: '2 oz',
        category: 'spirits',
      },
      {
        name: 'Chum Churum Soju',
        price: '$4.60',
        rating: 3,
        image: Soju,
        volume: '2 oz',
        category: 'spirits',
      },
    ],
  },
  {
    category: 'liqueurs',
    drinks: [
      {
        name: 'Jägermeister',
        price: '$4.05',
        rating: 4,
        image: Jagermeister,
        volume: '1 oz',
        category: 'liqueurs',
      },
      {
        name: 'Licor 43',
        price: '$7.05',
        rating: 3,
        image: Licor43,
        volume: '1 oz',
        category: 'liqueurs',
      },
      {
        name: 'Russo Limoncello',
        price: '$3.95',
        rating: 5,
        image: Limoncello,
        volume: '1 oz',
        category: 'liqueurs',
      },
    ],
  },
];

export const allDrinks = info.map(({ category, drinks }) => drinks).flat(1);

export default info;
