import { Box, Button, Checkbox, createStyles, TextInput, Group } from '@mantine/core';
import { useForm } from '@mantine/form';

const useStyles = createStyles(() => ({
  container: {
    marginBottom: 100,
  },
}));


function Drinks() {
    const form = useForm({
        initialValues: {
          ing1: '',
          ing2: '',
          d1: '',
          d2: '',
          termsOfService: false,
        },
      });
    
      return (
          <form onSubmit={form.onSubmit((values) => console.log(values))}>
           
            <Group spacing="xs">
                
            <TextInput
              label="Ingredients"
              placeholder=""
              {...form.getInputProps('ing2')}
            />
            <TextInput
              label=""
              placeholder=""
              {...form.getInputProps('d2')}
            />
            </Group>
            <Group spacing="xs">
                
            <TextInput
              label=""
              placeholder="Ingredient 2"
              {...form.getInputProps('ing2')}
            />
            <TextInput
              label=""
              placeholder="Dispenser 2"
              {...form.getInputProps('d2')}
            />
            </Group>
            <Group spacing="xs">
                
            <TextInput
              label=""
              placeholder="Ingredient 2"
              {...form.getInputProps('ing2')}
            />
            <TextInput
              label=""
              placeholder="Dispenser 2"
              {...form.getInputProps('d2')}
            />
            </Group>
            <Group spacing="xs">
                
            <TextInput
              label=""
              placeholder="Ingredient 2"
              {...form.getInputProps('ing2')}
            />
            <TextInput
              label=""
              placeholder="Dispenser 2"
              {...form.getInputProps('d2')}
            />
            </Group>
            <Group position="right" mt="md">
              <Button type="submit">Submit</Button>
            </Group>
          </form>
      );
}

export default Drinks;
